$(function(){$("#reserveList").on("click",".detail",function(e){e.preventDefault(),$$.modal.show({title:"备注信息",content:$(this).data("text"),type:"info"})}).on("click",".accept",function(e){e.preventDefault();var t=$(this),a=t.closest("tr"),n=a.data("id");$.ajax({url:"/keeper/reserve/"+n,type:"POST",dataType:"json",data:{_method:"PUT",reserveStatus:1}}).done(function(e){e.state&&window.location.reload()})}).on("click",".ignore",function(e){e.preventDefault();var t=$(this),a=t.closest("tr"),n=a.data("id");$.ajax({url:"/keeper/reserve/"+n,type:"POST",dataType:"json",data:{_method:"PUT",reserveStatus:2}}).done(function(e){e.state&&window.location.reload()})}).on("click",".delete",function(e){e.preventDefault();var t=$(this),a=t.closest("tr"),n=a.data("id");$.ajax({url:"/keeper/reserve/"+n,type:"DELETE",dataType:"json"}).done(function(e){e.state&&window.location.reload()})})});